ALL_SERVICES_COMPANIES = IT_SERVICES_COMPANIES + REMOTE_FIRST_COMPANIES


def seed_it_services():
    """Seed IT Services companies"""
    logger.info("üè¢ Seeding IT Services and Remote-First companies...")
    init_db()
    db = SessionLocal()
    
    added = 0
    updated = 0
    
    try:
        for company_data in ALL_SERVICES_COMPANIES:
            slug = company_data.get("slug")
            existing = db.query(Company).filter(Company.slug == slug).first()
            
            if existing:
                for key, value in company_data.items():
                    setattr(existing, key, value)
                updated += 1
            else:
                company = Company(**company_data)
                db.add(company)
                added += 1
        
        db.commit()
        logger.info(f"‚úÖ Added {added} new companies, Updated {updated}")
        
        total = db.query(Company).count()
        logger.info(f"üìä Total companies in database: {total}")
        
    except Exception as e:
        logger.error(f"‚ùå Error: {e}")
        db.rollback()
    finally:
        db.close()


if __name__ == "__main__":
    seed_it_services()




