def seed_global_tech():
    """Seed global tech companies"""
    logger.info("üåç Seeding Phase 2: Global Tech Giants...")
    init_db()
    db = SessionLocal()
    
    added = 0
    updated = 0
    
    try:
        for company_data in GLOBAL_TECH_GIANTS:
            slug = company_data.get("slug")
            existing = db.query(Company).filter(Company.slug == slug).first()
            
            if existing:
                for key, value in company_data.items():
                    setattr(existing, key, value)
                updated += 1
            else:
                company = Company(**company_data)
                db.add(company)
                added += 1
        
        db.commit()
        logger.info(f"‚úÖ Phase 2: Added {added} new companies, Updated {updated}")
        
        total = db.query(Company).count()
        logger.info(f"üìä Total companies in database: {total}")
        
    except Exception as e:
        logger.error(f"‚ùå Error: {e}")
        db.rollback()
        raise
    finally:
        db.close()


if __name__ == "__main__":
    seed_global_tech()


